INSERT INTO `productos` (`id`, `nombre`, `descripcion`, `imagen`, `id_categoria`) VALUES
(22, 'ACONDICIONADOR DE MUESTRAS', 'ud medida: °C (grados Celsius, para temperatura), % (porcentaje de humedad), L/min (litros por minuto, para flujo de aire), Pa (pascales, para presión si aplica). Acondicionador de muestras: dispositivo que prepara muestras para análisis, controlando temperatura, humedad, mezclando o filtrando. ', '20240808213059.jpg', 16),
(23, 'ACUMULADOR CARGA PARA MAQUINA UNIVERSAL DE ENSAYOS', 'ud medida: kN (kilonewtons, para capacidad de carga), mm (milímetros, para desplazamiento o longitud), MPa (megapascales, para presión), kg (kilogramos, para peso del acumulador). Estabiliza la fuerza aplicada y mejora la precisión en ensayos de materiales, pruebas de resistencia y análisis de fatiga.', '20240808213435.jpg', 16),
(24, 'AEROENFRIADOR COOLER', 'ud medida: kW (kilovatios, para capacidad de enfriamiento), m³/h (metros cúbicos por hora, para flujo de aire), °C (grados Celsius, para temperatura), dB (decibelios, para nivel de ruido), L/min (litros por minuto, para flujo de agua). Dispositivo que enfría aire o fluidos mediante convección, evaporación o intercambio de calor, utilizado en refrigeración industrial, climatización y procesos químicos. ', '20240808213537.jpg', 16),
(25, 'AGITADOR', 'ud medida: rpm (revoluciones por minuto, para velocidad de agitación), L (litros, para volumen de mezcla), N·m (newton-metros, para torque), °C (grados Celsius, para temperatura si aplica). Tipos de agitadores: Ancla (viscoso), Hélice (líquido), Pala (baja viscosidad), Turbina (alta velocidad), Dispersor (partículas finas). Cada uno adapta a necesidades específicas de mezclado.', '20240822215850.jpg', 16),
(26, 'AIRE ACONDICIONADO DE PRECISION', 'ud medida: kW (kilovatios, para capacidad de enfriamiento), m³/h (metros cúbicos por hora, para flujo de aire), °C (grados Celsius, para temperatura), % (porcentaje de humedad controlada), dB (decibelios, para nivel de ruido), Pa (pascales, para presión estática). Sistema de aire acondicionado de precisión para racks, filas, habitaciones, con control climático exacto. ', '20240808213700.jpg', 16),
(27, 'AIRE ACONDICIONADO TIPO PAQUETE', 'ud medida: kW (kilovatios, para capacidad de enfriamiento o calefacción), m³/h (metros cúbicos por hora, para flujo de aire), °C (grados Celsius, para temperatura), dB (decibelios, para nivel de ruido), % (porcentaje de eficiencia energética). Sistema de aire acondicionado compacto, con unidad paquete y funcional, para refrigeración, calefacción y control de humedad, ideal para espacios pequeños. ', '20240808213735.jpg', 16),
(28, 'AIRE ACONDICIONADO TIPO SPLIT CENTRAL', 'ud medida: kW (kilovatios, para capacidad de enfriamiento o calefacción), m³/h (metros cúbicos por hora, para flujo de aire), °C (grados Celsius, para temperatura), dB (decibelios, para nivel de ruido), % (porcentaje de eficiencia energética). Sistema de aire acondicionado silencioso y eficiente, con unidad central y funcional, para refrigeración, calefacción, control de humedad y purificación del aire.', '20240808213802.jpg', 16),
(29, 'AIREADOR', 'Dispositivo que incrementa oxígeno en agua, suelo o industria, mejorando calidad y salud. Unidad de medida: m³/h (metros cúbicos por hora, para caudal de aire), Pa (pascales, para presión), dB (decibelios, para nivel de ruido), °C (grados Celsius, para temperatura del aire).', '20240808215403.jpg', 16),
(30, 'ALESADORA HORIZONTAL', 'ud medida: mm (milímetros, para desplazamiento o longitud de corte), rpm (revoluciones por minuto, para velocidad de rotación), kW (kilovatios, para potencia), mm/min (milímetros por minuto, para velocidad de avance). Máquina para mezclar y amasar masas, ideal para panadería, pastelería e industria alimentaria, con movimiento horizontal y capacidad variable. ', '20240808215432.jpg', 16),
(31, 'ANALIZADOR DE ACEITE', 'ud medida: % (contenido de agua o contaminantes), mg/kg (miligramos por kilogramo, para impurezas), °C (temperatura de punto de inflamación o viscosidad), ppm (partes por millón, para aditivos o contaminantes). Dispositivo que examina propiedades de aceites como viscosidad, pureza e impurezas para evaluar calidad y condiciones en mantenimiento industrial, alimentos y refinerías. ', '20240808215459.jpg', 16),
(32, 'ANALIZADOR DE ACIDO', 'Dispositivo que mide concentración de ácidos en soluciones, como pH y acidez total, para controlar procesos industriales, analizar alimentos y evaluar agua.', '20240808215551.jpg', 16),
(33, 'ANALIZADOR DE BATERIAS', 'ud medida: V (voltios), A (amperios), Ah (amperios-hora), Ω (ohmios, para resistencia interna), °C (grados Celsius, para temperatura). Dispositivo que evalúa baterías midiendo voltaje, corriente, capacidad y resistencia, para detectar problemas y optimizar mantenimiento. ', '20240808215617.jpg', 16),
(34, 'ANALIZADOR DE CARBONO TOTAL', 'ud medida: mg/L (miligramos por litro), ppm (partes por millón), % (porcentaje de carbono).Instrumento que mide la cantidad total de carbono en una muestra, incluyendo carbono orgánico e inorgánico, para evaluar calidad de agua, suelo y ecosistemas. ', '20240822232033.jpg', 16),
(35, 'ANALIZADOR DE COMBUSTIBLE', 'ud medida: % (contenido de componentes), mg/L (miligramos por litro, para impurezas), ppm (partes por millón, para aditivos o contaminantes), °C (temperatura de punto de inflamación). Dispositivo que examina propiedades de combustibles para determinar calidad, pureza, composición química y contenido de impurezas, evaluando rendimiento y eficiencia. ', '20240808215742.jpg', 16),
(36, 'ANALIZADOR DE COMPUESTOS QUIMICOS', 'ud medida: ppm (partes por millón), % (concentración en peso o volumen), mg/L (miligramos por litro), µg/m³ (microgramos por metro cúbico). Instrumento que identifica y mide compuestos químicos en una muestra, utilizando técnicas como cromatografía y espectrometría, para analizar composición química y detectar contaminantes. ', '20240808215800.jpg', 16),
(37, 'ANALIZADOR DE CONDUCTIVIDAD', 'ud medida: µS/cm (microsiemens por centímetro), mS/cm (milisiemens por centímetro). Dispositivo que mide la capacidad de una sustancia para conducir electricidad, expresada en Siemens por metro (S/m), utilizado para evaluar pureza del agua, detectar iones y contaminantes. ', '20240808215822.jpg', 16),
(38, 'ANALIZADOR DE REDES ELECTRICAS', 'ud medida: V (voltios), A (amperios), W (vatios), Hz (hercios), kVA (kilovoltamperios), Ω (ohmios), % (factor de potencia). Herramienta que examina redes eléctricas para detectar fallas, medir voltaje y corriente, y evaluar la calidad de la energía, permitiendo identificar problemas y optimizar el rendimiento. ', '20240808224643.jpg', 16),
(39, 'ANALIZADOR DE RESISTENCIA AL GEL ESTATICO', 'ud medida: Pa (pascales, para resistencia al gel), °C (grados Celsius, para temperatura si aplica), % (porcentaje de deformación). Instrumento que mide la resistencia de un material, como un polímero o un lubricante, a la degradación causada por la energía estática, evaluando su capacidad para mantener sus propiedades físicas y químicas.', '20240808224704.jpg', 16),
(40, 'ANALIZADOR DE TEXTURAS', 'ud medida: g (gramos de fuerza), N (newtons), mm (milímetros, para elongación o deformación), ° (grados, para ángulo de deformación). Instrumento que mide propiedades texturales de materiales, como dureza, suavidad y rugosidad, para caracterizar y clasificar texturas en alimentos, cosméticos, materiales de construcción, etc. ', '20240821175152.jpg', 16),
(41, 'ANALIZADOR DE TOXICIDAD', 'ud medida: mg/L (miligramos por litro), ppm (partes por millón), µg/L (microgramos por litro), % (concentración o inhibición). Dispositivo que mide la toxicidad o peligrosidad de sustancias químicas, agua o aire, utilizando bioensayos o sensores para detectar la presencia y nivel de toxinas, permitiendo evaluar el riesgo para la salud humana y el medio ambiente. ', '20240808224748.jpg', 16),
(42, 'ANALIZADOR HEMATOLOGICO AUTOMATIZADO', 'ud medida: células/µL (células por microlitro), % (porcentaje), g/dL (gramos por decilitro), mm³ (milímetros cúbicos), % (porcentaje de hematíes, leucocitos, etc.). Dispositivo médico que analiza muestras de sangre para determinar parámetros hematológicos, como recuento de células, hemoglobina y morfología, de manera automática y rápida. ', '20240808224806.jpg', 16),
(43, 'ANALIZADOR INFRARROJO', 'ud medida: % (concentración), mg/L (miligramos por litro), ppm (partes por millón), cm⁻¹ (número de ondas), °C o °F (temperatura, si aplica). Instrumento que utiliza la espectroscopía infrarroja para analizar la composición química de sustancias, midiendo la absorción o reflexión de radiación infrarroja por las moléculas, permitiendo identificar y cuantificar compuestos químicos. ', '20240808224908.jpg', 16),
(44, 'ANALIZADOR MULTIFUNCION', 'ud medida: unidades de pH, µS/cm o mS/cm (conductividad), mg/L o % (oxígeno disuelto), °C o °F (temperatura), NTU (turbidez), mV (redox), % (concentración de otros parámetros según la función). Instrumento versátil que combina varias funciones de análisis en una sola unidad, como análisis de gases, líquidos y sólidos, permitiendo realizar múltiples pruebas y mediciones con un solo dispositivo. ', '20240808224929.jpg', 16),
(45, 'ANALIZADOR MULTIPARAMETRO', 'ud medida: unidades de pH, µS/cm o mS/cm (conductividad), mg/L o % (oxígeno disuelto), °C o °F (temperatura), NTU (turbidez), mV (redox). Instrumento que mide y analiza múltiples parámetros físicos o químicos simultáneamente, como pH, temperatura, conductividad, oxígeno disuelto, etc., en una sola unidad, permitiendo un monitoreo integral de procesos o condiciones. ', '20240808225005.jpg', 16),
(46, 'ANALIZADOR PUNTO DE NUBE', 'ud medida: °C (temperatura de punto de nube), °F, % en peso o ppm (concentración de contaminantes). Dispositivo que determina la temperatura a la cual un líquido, como el combustible diesel, comienza a formar cristales de cera o \"nubes\" de partículas, indicando su fluidez y rendimiento en condiciones frías. ', '20240808225025.jpg', 16),
(47, 'ANALIZADOR QUIMISORCION', 'ud medida: mg (cantidad de adsorbato), m²/g (área de superficie específica), cm³/g (volumen de poro), nm (tamaño de poro). Instrumento que mide la cantidad de sustancias químicas absorbidas por un material o sustancia, como la humedad o gases, utilizando sensores o técnicas de espectroscopía. ', '20240823142806.jpg', 16),
(48, 'ANALIZADOR SAL EN CRUDO', 'ud medida: % (concentración de sal), ppm (partes por millón). Dispositivo que mide el contenido de sal en petróleo crudo, importante para evaluar la calidad y procesabilidad del crudo. Utiliza técnicas como conductividad o refractometría.', '20240808225059.jpg', 16),
(49, 'ANALIZADOR TAMAÑO DE PARTICULAS', 'ud medida: µm (tamaño de partículas), nm (tamaño de partículas), partículas/L (número de partículas), mg/m³ o ppm (concentración). Instrumento que mide y analiza el tamaño y distribución de partículas en una suspensión o polvo, utilizando técnicas como difracción de luz o sedimentación.', '20240808225114.jpg', 16),
(50, 'BAÑO DE BLOQUE SOLIDO PARA GOMA', 'ud medida: °C (temperatura), minutos u horas (tiempo), litros o galones (volumen), L/min o GPM (flujo). Dispositivo que calienta y acelera la curación de la goma, utilizando un bloque sólido caliente para aplicar presión y temperatura uniformes. ', '20240808234202.jpg', 16),
(51, 'BAÑO DE CALENTAMIENTO', 'ud medida: °C (temperatura), minutos u horas (tiempo), litros o galones (volumen), L/min o GPM (flujo), bar o psi (presión). Dispositivo que calienta y mantiene la temperatura de sustancias o muestras a un nivel constante, generalmente utilizado en laboratorios y procesos industriales.', '20240821175241.jpg', 16),
(52, 'BAÑO DE ENFRIAMIENTO Y RECIRCULACION', 'Sistema de enfriamiento y recirculación de fluido para control de temperatura.', '20240808234234.jpg', 16),
(53, 'BAÑO DE ESTABILIDAD ALTA OXIDACION EN GASOLINA', 'ud  medida: °C (temperatura), horas o minutos (tiempo), bar o psi (presión), litros o galones (volumen).Prueba para evaluar la resistencia de la gasolina a la oxidación y la formación de depósitos a altas temperaturas, simulando condiciones extremas de almacenamiento y uso. ', '20240808234307.jpg', 16),
(54, 'BAÑO DE PARAFINA', 'Recipiente con parafina líquida para calentar y tratar tejidos o muestras a una temperatura suave (alrededor de 60°C), utilizado en laboratorios y aplicaciones médicas. Unidad de medida: °C (temperatura), minutos u horas (tiempo), litros o galones (volumen), L/min o GPM (flujo).', '20240808234327.jpg', 16),
(55, 'BAÑO DE REFRIGERACION', 'Recipiente o cámara que enfría y mantiene la temperatura de sustancias o muestras a un nivel bajo, generalmente por debajo de 0°C. Unidad de medida: °C (temperatura), minutos u horas (tiempo), litros o galones (volumen), L/min o GPM (flujo).', '20240822215951.jpg', 16),
(56, 'BAÑO DE VISCOSIDAD', 'ud medida para un baño de viscosidad es grados Celsius (°C), que indica la temperatura a la que se realiza la medición de la viscosidad de líquidos. Dispositivo para medir viscosidad de líquidos a diferentes temperaturas. ', '20240808234456.jpg', 16),
(57, 'BAÑO PARA CORROSION', 'ud medida para un baño para corrosión es grados Celsius (°C), que indica la temperatura a la que se mantiene el baño para pruebas de corrosión. Recipiente o cámara utilizada para probar la resistencia a la corrosión de materiales y productos, sometiéndolos a condiciones controladas de humedad, temperatura y exposición a sustancias corrosivas. ', '20240808234511.jpg', 16),
(58, 'BAÑO SEROLOGICO', 'Equipo utilizado en laboratorios para incubar y almacenar muestras de sueros, vacunas y otros materiales biológicos a una temperatura controlada (generalmente entre 2-8°C), manteniendo su estabilidad y viabilidad. La unidad de medida para un baño serológico también es **grados Celsius (°C)**, que indica la temperatura a la que se mantiene el baño para pruebas serológicas.', '20240808234528.jpg', 16),
(59, 'BAÑO TERMOSTATADO', 'ud medida: para un baño termostatado es grados Celsius (°C) o grados Fahrenheit (°F), que indica la temperatura del baño. Dispositivo para mantener temperatura constante, usado en laboratorios y procesos industriales.', '20240808234547.jpg', 16),
(60, 'BARRAJE CCM TIPO EXTERIOR', 'ud medida: (Control de Condiciones del Medio) tipo exterior se mide en ohmios (Ω).Conjunto de baterías de ciclo cerrado (CCM) instalado en un lugar externo, utilizado para almacenar energía generada por fuentes renovables o para proporcionar energía. ', '20240808234800.jpg', 16),
(61, 'BARRAJE DE MT SWG', 'ud medida: (medidor de temperatura) SWG (Standard Wire Gauge) se mide en ohmios (Ω).Conjunto de baterías conectadas en serie (SW) y paralelo (G) para aumentar la tensión y capacidad total de almacenamiento de energía, utilizado en aplicaciones que requieren alta potencia y autonomía. El barraje de MT (medidor de temperatura) SWG (Standard Wire Gauge) se mide en ohmios (Ω).', '20240821175608.jpg', 16),
(62, 'BASCULA', 'ud medida de una báscula es kilogramos (kg) o gramos (g), dependiendo de la precisión y el tipo de báscula. Instrumento que mide la masa o peso de un objeto o sustancia, comúnmente utilizado en laboratorios, industrias y aplicaciones comerciales. ', '20240808234842.jpg', 16),
(63, 'BARRAJE DE BT PC', 'ud medida suele ser ohmios (Ω). Conjunto de baterías conectadas en paralelo (PC) para aumentar la capacidad total de almacenamiento de energía, utilizado en aplicaciones que requieren alta autonomía y fiabilidad. Para el barraje de BT (barraje de bobina de transformador) en una PC,', '20240808234926.jpg', 16),
(64, 'BILIRRUBINOMETRO', 'ud medida para un bilirrubinómetro es mg/dL (miligramos por decilitro) o μmol/L (micromoles por litro), dependiendo del sistema de medida utilizado.Dispositivo que mide la concentración de bilirrubina en la sangre, utilizado para diagnosticar y monitorear condiciones hepáticas y enfermedades relacionadas.', '20240808235006.jpg', 16),
(65, 'BIORREACTOR', 'ud medida: litros (L) para el volumen de cultivo, o revoluciones por minuto (RPM) para la velocidad de agitación. Dispositivo que controla condiciones para cultivo de células, microorganismos o tejidos, utilizado en biotecnología, investigación y producción de productos biológicos. ', '20240823182154.jpg', 16),
(66, 'BOMBA CENTRIFUGA', 'ud medida: litros por minuto (L/min) para el caudal, o metros de columna de agua (m) para la altura de elevación. Bomba que utiliza la fuerza centrífuga para impulsar fluidos, creando una alta velocidad y presión.', '20240808235050.jpg', 16),
(67, 'BOMBA DE CAVIDAD PROGRESIVA PCP', 'ud medida: litros por minuto (L/min) para el caudal, o bares (bar) para la presión. Bomba que utiliza cavidades en forma de tornillo para mover fluidos, ideal para aplicaciones de alta viscosidad y presión constante. ', '20240808235108.jpg', 16),
(68, 'BOMBA DE INFUSION', 'ud medida: mililitros por hora (mL/h) para el flujo de infusión, o litros (L) para la capacidad del reservorio. Dispositivo que administra líquidos a una velocidad controlada y constante, comúnmente utilizada en medicina para infusión de medicamentos o soluciones. ', '20240808235122.jpg', 16),
(69, 'BOMBA DE TORNILLO', 'ud medida: litros por minuto (L/min) para el caudal, o bares (bar) para la presión. Bomba que utiliza uno o más tornillos rotativos para mover fluidos, ideal para aplicaciones de alta viscosidad y presión. ', '20240808235140.jpg', 16),
(70, 'BOMBA DE VACIO', 'ud medida: milibares (mbar) o pascales (Pa) para la presión de vacío, o litros por minuto (L/min) para el caudal de evacuación. Dispositivo que genera vacío para mover fluidos, gases o materiales. ', '20240808235206.jpg', 16),
(71, 'BOMBA DESPLAZAMIENTO POSITIVO BOMBEO MEC', 'ud medida: litros por minuto (L/min) para el caudal, o bares (bar) para la presión. Bomba que desplaza un volumen fijo de fluido por revolución, ideal para aplicaciones de alta precisión y control en procesos industriales y mecánicos. ', '20240808235222.jpg', 16),
(72, 'BOMBA PERIFERICA', 'ud medida: litros por minuto (L/min) para el caudal, o metros de columna de agua (m) para la altura de elevación. Dispositivo que utiliza un rotor periférico para impulsar fluidos, ideal para alto caudal y baja presión. ', '20240808235239.jpg', 16),
(73, 'ANALIZADOR DE GAS', 'ud medida: porcentaje (%) para la concentración de gases, o partes por millón (ppm) para niveles más bajos de concentración. Dispositivo que mide la composición y concentración de gases en un ambiente o muestra, utilizado en diversas industrias para monitorear y detectar gases. ', '20240820231404.jpg', 16),
(74, 'ANALIZADOR DE HUMEDAD', 'ud medida: porcentaje (%) para la humedad relativa, o gramos por kilogramo (g/kg) para el contenido de humedad. Dispositivo que mide la humedad en un ambiente o material, utilizado en diversas industrias para controlar y monitorear niveles de humedad. ', '20240809001414.jpg', 16),
(75, 'BOMBA RECIPROCANTE', 'ud medida: litros por minuto (L/min) para el caudal, o bares (bar) para la presión. Bomba que utiliza un pistón que se mueve hacia adelante y hacia atrás para bombear fluidos, ideal para aplicaciones de alta presión y caudal variable en industrias como la petrolera y química. ', '20240809144814.jpg', 16),
(76, 'BOMBA ROTATIVA DE ENGRANAJES', 'ud medida: litros por minuto (L/min) para el caudal, o bares (bar) para la presión. Bomba que utiliza engranajes rotativos para bombear fluidos, proporcionando un flujo constante y preciso en aplicaciones industriales, químicas y petroleras.', '20240809144828.jpg', 16),
(77, 'BOMBA ROTATIVA DE LOBULOS', 'ud medida: litros por minuto (L/min) para el caudal, o bares (bar) para la presión. Bomba que utiliza lobulos rotativos para trasladar fluidos, ofreciendo alta eficiencia y suavidad en aplicaciones industriales, farmacéuticas y alimentarias. ', '20240809144841.jpg', 16),
(78, 'BOMBA ROTATIVA DE PALETAS', 'ud medida: litros por minuto (L/min) para el caudal, o bares (bar) para la presión. Bomba que utiliza paletas rotativas para mover fluidos, ideal para manejar líquidos viscosos y mantener un flujo constante en aplicaciones industriales. ', '20240821145916.jpg', 16),
(79, 'BOMBA ROTATIVA DE PISTONES', 'ud medida: litros por minuto (L/min) para el caudal, o bares (bar) para la presión. Equipo que utiliza pistones rotativos para bombear fluidos, ofreciendo alta eficiencia y precisión en aplicaciones industriales y de procesos. ', '20240809144914.jpg', 16),
(80, 'BRAZO DE LLENADO', 'ud medida: litros por minuto (L/min) para la tasa de flujo de llenado, o metros (m) para la longitud del brazo. Mecanismo que llena recipientes con precisión y velocidad, utilizado en industrias de envasado, farmacéutica y cosmética.', '20240822220020.jpg', 16),
(81, 'CABEZAS LASER DE ALINEACION', 'ud medida: micrómetros (µm) para la precisión de alineación, o milivatios (mW) para la potencia del láser. Dispositivos que emiten haces láser para alinear objetos o superficies con precisión, utilizados en industria, construcción y medicina. ', '20240809145048.jpg', 16),
(82, 'CABINA AUDIOMETRICA', 'ud medida: decibelios (dB) para el nivel de insonorización, o metros cuadrados (m²) para la superficie del suelo. Habitáculo insonorizado para realizar pruebas de audición y medición de la capacidad auditiva, minimizando el ruido exterior y garantizando resultados precisos. ', '20240809145102.jpg', 16),
(83, 'CABINA DE ANALIZADORES', 'ud medida: metros cúbicos (m³) para el volumen interno, o grados Celsius (°C) para la temperatura, dependiendo de las condiciones requeridas para los analizadores. Espacio diseñado para albergar y proteger equipos de análisis y medición, manteniendo un ambiente controlado y reduciendo interferencias. ', '20240809145120.jpg', 16),
(84, 'CABINA DE FLUJO LAMINAR', 'ud medida: metros cúbicos por hora (m³/h) para el flujo de aire, o centímetros por segundo (cm/s) para la velocidad del flujo de aire dentro de la cabina.\r\nEquipo que proporciona un flujo de aire limpio y controlado, utilizado en laboratorios y hospitales para trabajar con materiales sensibles y prevenir contaminación. ', '20240823142911.jpg', 16),
(85, 'CABINA DE LAVADO', 'ud medida: litros por minuto (L/min) para el flujo de agua, o metros cúbicos (m³) para el volumen interno de la cabina. Espacio para limpiar objetos o personas, utilizado en laboratorios, industrias y hospitales.', '20240809145213.jpg', 16),
(86, 'CALDERA ELECTRICA', 'ud medida: kilovatios (kW) para la potencia, o grados Celsius (°C) para la temperatura. Equipo que genera calor eléctrico para producir agua caliente o vapor, utilizado en calefacción y procesos industriales. ', '20240809145233.jpg', 16),
(87, 'CALENTADOR A FUEGO DIRECTO', 'ud medida: grados Celsius (°C) para la temperatura, o kilocalorías por hora (kcal/h) para la capacidad de calentamiento. Dispositivo que utiliza llama abierta para calentar objetos o sustancias, proporcionando calor intenso y directo. ', '20240809145442.jpg', 16),
